import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from 'material-ui/styles';
import Card from 'material-ui/Card';

const styles = () => ({
  container: {
    width: '100%',
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    textAlign: 'center',
  },
});

function GradientPanel(props) {
  return (
    <Fragment>
      <Card
        className={props.classes.container}
        style={{ backgroundImage: props.gradientStyle, height: `${props.heightGradientPanel}px` }}
      />
    </Fragment>
  );
}

GradientPanel.defaultProps = {
  gradientStyle: '',
  heightGradientPanel: 0,
};

GradientPanel.propTypes = {
  gradientStyle: PropTypes.string.isRequired,
  heightGradientPanel: PropTypes.number.isRequired,
};

export default withStyles(styles)(GradientPanel);
